-- Respawn Pure Water 182951 and Add Pooling
SET @OGUID := 190700;
DELETE FROM `gameobject` WHERE `id` = 182951;

DELETE FROM `pool_template` WHERE `entry` IN (13993,13994);

DELETE FROM `pool_gameobject` WHERE `guid` BETWEEN 190700 AND 190741;
SELECT guid, 13993, 0, 'Zangarmarsh - Pure Water (182951)'
FROM `gameobject`
WHERE `guid` BETWEEN @OGUID + 01 AND @OGUID + 05;

SELECT guid, 13994, 0, 'Nagrand - Pure Water (182951)'
FROM `gameobject`
WHERE `guid` BETWEEN @OGUID + 06 AND @OGUID + 41;

